AFRAME.registerComponent("volume",{init:function(){var e=document.querySelector("#control-volume"),t=document.querySelector("#control-mute"),u=document.querySelector("#vid1");u.muted=!0,this.el.addEventListener("click",function(i){!0===u.muted?(u.muted=!1,e.setAttribute("visible","true"),t.setAttribute("visible","false")):!1===u.muted&&(u.muted=!0,e.setAttribute("visible","false"),t.setAttribute("visible","true"))},!1)}});
